import mqttimport wifiimport machinefrom utime import sleep_msimport motorsled = machine.Pin('LED', machine.Pin.OUT)led.on()sleep_ms(1500)led.off()wlan = wifi.connect()print("Wifi almost connected")while wlan.status() != 3 or not wlan.isconnected():  passprint("WIFI fully connected")led.on()mqtt_server = 'siur123.pl'port = 18833client_id = 'toDurnePico'topic = '#'def msgCallback(topic, message):  print("Topic: ", topic)  print("Message: ", message)  motors.callback(topic, message)  #if 'ON' in message:  #  led.on()  #elif 'OFF' in message:  #  led.off()  client = mqtt.MQTTClient(client_id, mqtt_server, keepalive=3600, port=18833)client.connect()client.set_callback(msgCallback)client.subscribe('#')print("SETUP COMPLETE")client.publish('pl/aaa', 'whatever')def receiveLoop():  while True:    print("Waiting for msg")    client.wait_msg()    def printLoop():  while True:    print("AAAAAAA")    sleep_ms(5000)#import _thread#_thread.start_new_thread(printLoop, ())receiveLoop()